            post: post,
            comments: commentsFiltered,
          };

          postsArray.push(postObject);
        });
        console.log(postsArray);
      } 
    }

    getDatsAPI();

     setTimeout(e=>{
        matchData();
    },5000);

  }, []);

  return <div></div>;
};

export default Index;
