import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPosts } from "../actions/PostsActions";
import { getComments } from "../actions/CommentsActions";

const Index = () => {
  const dispatch = useDispatch();

  const [post, Posts] = useState([]);
  const [comments, Comments] = useState([]);

  useEffect(() => {
    /**
     * Gets data from API
     */
    function getDatsAPI() {
      //Getting posts data
      const postsList = () => dispatch(getPosts());
      Posts(postsList);
      //Getting comments data
      const commentsList = () => dispatch(getComments());
      Comments(commentsList);
    }

    function matchData() {
      let postObject = {};
      let postsArray = [];

      if (Object.keys(posts).length > 0) {
        posts.map((post) => {
          let commentsFiltered = comments.filter(
            (e) => e.postid === post.postid
          );

          postObject = {
            post: post,
            commets: commentsFiltered,
          };

          postsArray.push(postObject);
        });
      }
    }

    getDatsAPI();
    matchData();
  }, []);

  return <div></div>;
};

export default Index;
