import React,{useState} from 'react';

const Comment = ({comment}) => {
    return (
        <div>
            <p className="font-weight-bold">{comment.name}</p>
            <p>{comment.email}</p>
            <p className="font-italic">{comment.body}</p>
        </div>
    );
};

export default Comment;
