import React,{useState} from "react";
import {AddNewComment} from '../actions/CommentsActions';
import { useDispatch, useSelector } from "react-redux";

const NewComment = () => {

    const dispatch = useDispatch();
    const SvdNewComment = (comment) => dispatch(AddNewComment(comment));

    const postsData = useSelector((state) => state.commentsState.commenta);


    const [comment,SvdComment] = useState({
        postId:0,
        id:0,
        name:'',
        email:'',
        body:''
    });

 const addComment = e =>{

    e.preventDefault();

    const {name,email,body} = comment;

    if(name != '' || email != '' || body != ''){
        console.log('Incomplete Data');
        return;
    }

    SvdNewComment(comment);

 };

  return (
    <div className="mb-4"> 
      <form onSubmit={()=>addComment()}>
        <label>Name: </label>
        <input className="mb-1" name='name' type="text" onChange={e=>SvdComment(e.target.value)} />
        <label>Email: </label>
        <input className="mb-1"  name='email' type="text" onChange={e=>SvdComment(e.target.value)}/>
        <label>Body: </label>
        <input className="mb-1"  name='body' type="text-area" onChange={e=>SvdComment(e.target.value)}/>
        <input type="button" value="Add Comment" />
      </form>
    </div>
  );
};

export default NewComment;
