import React from "react";
import Post from './Post';


const Card = ({postData}) => {

    const {comments,post} = postData;
    const {id,title,body} = post;

  return (
      <div className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
            <div className="card">
            <p className="font-weight-bold p-3 text-uppercase" >{title}</p>
            <div className="card-body">
                    <p className="card-text text-capitalize">{body}</p>
                </div>
                <div className="card-footer mx-auto p-2">
                  {display ? <CommentList key={id} comments = {comments}/> : <button onClick={()=>displayComments(!display)}>See Comments</button>} 
                  {display ? <button onClick={()=>displayComments(!display)}>Hide Comments</button> : null}
                </div>
        </div>
         <Post postData={postData} />
      </div>
  );
};

export default Card;
