import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPosts } from "../actions/PostsActions";
import { getComments } from "../actions/CommentsActions";

const Index = () => {
  const dispatch = useDispatch();

  const postsData = useSelector((state) => state.postsState.posts);
  const commentsData = useSelector((state) => state.commentsState.comments);

  const [matchedData, MatchData] = useState([]);

  useEffect(() => {
    /**
     * Gets data from API
     */
    function getDatsAPI() {
      //Getting posts data
      dispatch(getPosts());
      //   Posts(postsList);
      //Getting comments data
      dispatch(getComments());
      //   Comments(commentsList);
    }

    /**
     * Match posts and their right comments
     */
    function matchData() {

      if (Object.keys(postsData).length > 0) {
        postsData.map((post) => {

          let commentsFiltered = commentsData.filter((e) => e.postid === post.postid);

          MatchData({
            post: post,
            comments: commentsFiltered,
          });

        });
      }
    }

    getDatsAPI();

    setTimeout(() => {
      matchData();
    }, 5000);
  }, []);

  return (<div>
      {matchData()}
  </div>);
};

export default Index;
