import { getComments } from "../actions/CommentsActions";

const Index = () => {

  const dispatch = useDispatch();

  const posts = useSelector(state => state.postsState.posts);
  const comments = useSelector(state => state.commentsState.comments);

  const [matchedData, matchData] = useState([]);
//   const [comments, Comments] = useState([]);

  useEffect(() => {
    /**
     * Gets data from API
     */
    function getDatsAPI() {
      //Getting posts data
     dispatch(getPosts());
    //   Posts(postsList);
      //Getting comments data
     dispatch(getComments());
    //   Comments(commentsList);
    }

/**
 * Match posts and their right comments
 */
    function matchData() {

        debugger
      let postObject = {};
      let postsArray = [];

      if (Object.keys(posts).length > 0) {
        posts.map((post) => {
            debugger
          let commentsFiltered = comments.filter(
            (e) => e.postid === post.postid
          );

          postObject = {
            post: post,
            comments: commentsFiltered,
          };

          postsArray.push(postObject);
        });
        console.log(postsArray);
      } 
    }

    getDatsAPI();

    setTimeout(()=>{
        matchData();
    },5000);

  }, []);

  return <div></div>;
};

export default Index;
